import React, { useState, useRef, useEffect } from "react";
import FontAwesome from "react-fontawesome";
import "./dropDown.css";

const HideDropDown = (ref, state) => {
  useEffect(() => {
    /**
     * Alert if clicked on outside of element
     */
    function handleClickOutside(event) {
      if (ref.current && !ref.current.contains(event.target)) {
        alert("You clicked outside of me!");
      }
    }

    // Bind the event listener
    document.addEventListener("mousedown", handleClickOutside);
    return () => {
      // Unbind the event listener on clean up
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, [ref]);
};

const DropDown = ({ title, list }) => {
  let initialState = {
    listOpen: false,
    headerTitle: title,
  };

  const [state, setState] = useState(initialState);

  const toggleList = () => {
    setState((prevState) => ({
      listOpen: !prevState.listOpen,
      headerTitle: title,
    }));
  };

  const { headerTitle, listOpen } = state;

  const wrapperRef = useRef(null);

  HideDropDown(wrapperRef);

  return (
    <div className="dd-wrapper" ref={wrapperRef}>
      <div className="dd-header" onClick={() => toggleList()}>
        <div className="dd-header-title">{headerTitle}</div>
        {listOpen ? (
          <FontAwesome name="angle-up" size="lg" />
        ) : (
          <FontAwesome name="angle-down" size="lg" />
        )}
      </div>
      {listOpen && (
        <ul className="dd-list">
          {list.map((item) => (
            <li className="dd-list-item" key={item.id}>
              {item.title}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default DropDown;
